import React, { Component } from 'react';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Rank from './components/Rank/Rank';
import ParticlesBg from 'particles-bg';
import 'tachyons';
import './App.css';

// const IMAGE_URL = 'https://cdn0-production-images-kly.akamaized.net/oiwMGItCEPkWebOsd_PX3ylXRd8=/1200x1200/smart/filters:quality(75):strip_icc():format(jpeg)/kly-media-production/medias/3551964/original/052932000_1629966509-198866300_388468995863054_7712447264754457056_n.jpg';

class App extends Component {
  constructor(){
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
    }
  }

  calculateFAceLocation = (data) => {
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.querySelector('#inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    console.log(width, height)
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value});
  }

  onButtonSubmit = () => {
  this.setState({imageUrl: this.state.input});
  const USER_ID = 'jerryyip1999';
  const PAT = '84f1fe96264344bf9b0b0b00ffc6d555';
  const APP_ID = 'SmartBrain';

  const MODEL_ID = 'face-detection';
  const MODEL_VERSION_ID = '6dc7e46bc9124c5c8824be4822abe105';  
  const IMAGE_URL = this.state.input;

  const raw = JSON.stringify({
    "user_app_id": {
        "user_id": USER_ID,
        "app_id": APP_ID
    },
    "inputs": [
        {
            "data": {
                "image": {
                    "url": IMAGE_URL
                }
            }
        }
    ]
  });

  const requestOptions = {
    method: 'POST',
    headers: {
        'Accept': 'application/json',
        'Authorization': 'Key ' + PAT
    },
    body: raw
  };

  fetch("https://api.clarifai.com/v2/models/" + MODEL_ID + "/versions/" + MODEL_VERSION_ID + "/outputs", requestOptions)

  .then(response => response.json())
  .then(result => {
    this.calculateFAceLocation(result.outputs[0].data.regions[0].region_info.bounding_box)
  })
  .catch(error => console.log('error', error));

  }

  render(){
    return (
      <div className="App">
        <ParticlesBg type="circle" bg={true} />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm onInputChange={this.onInputChange} onButtonSubmit={this.onButtonSubmit}/>
        <FaceRecognition imageUrl={this.state.imageUrl} />
      </div>
    );
  }
}

export default App;
